var pdf = require('pdfkit')
var fs = require('fs')
var mysql      = require('mysql');

var connection = mysql.createConnection({
  host     : 'localhost',
  user     : 'root',
  password : '111111',
  database : 'new_database'
});

connection.connect();

connection.query('SELECT * from USER WHERE firstName == \'Cardinal\'', function(err, rows, fields) {
  if (!err)
    console.log('The solution is: ', rows);
  else
    console.log('Error while performing Query.');
});

connection.end();


var myDoc = new pdf;

myDoc.pipe(fs.createWriteStream('user_pdf'));

myDoc.font('Times-Roman')
	 .fontSize(20)
	 .text(rows, 100, 100)

myDoc.end();